package azureidentity_test

import (
	"crypto/x509"
	"testing"
	"time"

	"github.com/stretchr/testify/require"

	"github.com/coder/coder/coderd/azureidentity"
)

const (
	signature = ``
)

func TestValidate(t *testing.T) {
	t.Parallel()
	ct, err := time.Parse(time.RFC3339, "2023-02-01T00:00:00Z")
	require.NoError(t, err)
	vm, err := azureidentity.Validate(signature, x509.VerifyOptions{
		CurrentTime: ct,
	})
	require.NoError(t, err)
	require.Equal(t, "bd8e7443-24a0-41f3-b949-8baf4fd1c573", vm)
}
